INSERT INTO `movie_info` (`id`, `title`, `poster`, `address`, `content`, `visit`, `star`) VALUES (5231, '索尔之子 Saul fia', 'http://ww1.sinaimg.cn/mw690/005BjCpAgw1f1k9ug59j0j30b00gon07.jpg', 'https://apis.tianxianle.com/youku/?id=mXWZo6Cuo3eWnnJwlZxp&type=bilibili', '本片的故事发生在1944年二战期间的纳粹集中营，一个焚烧尸体的男子在“工作”的过程中，发现了自己儿子的尸体，于是情况发生了意想不到的变化。', NULL, NULL);
